,p_plsql_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'FUNCTION validate_user_DB(p_username VARCHAR2, p_password VARCHAR2) RETURN BOOLEAN AS',
'userrole varchar2(20);',
' cursor login_cur is',
'   select ''MEMBER''',
'   from member',
'   where upper(email) = upper(p_username) and upper(member_id) = upper(p_password)',
'UNION',
'   select ''STAFF''',
'   from staff',
'   where upper(username) = upper(p_username) and upper(password) = upper(p_password) and role = ''Staff''',
'',
'UNION',
'   select ''MANAGER'' ',
'   from staff',
'   where upper(username) = upper(p_username) and upper(password) = upper(p_password) and role = ''Manager'';',
'begin',
'open login_cur;',
'fetch login_cur into userrole; if login_cur%found then',
'apex_util.set_session_state(''user_role'',userrole);',
'return true; end if;',
'return false; end;',
''))
